import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import '../styles/Auth.css';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();

  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      await login(formData.email, formData.password);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.error || 'Error al iniciar sesi√≥n. Verifica tus credenciales.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        {/* Logo Banco de Bogot√° */}
        <div className="auth-logo">
          <img 
            src="https://upload.wikimedia.org/wikipedia/commons/thumb/5/5c/Banco_de_Bogot%C3%A1_logo.svg/1200px-Banco_de_Bogot%C3%A1_logo.svg.png" 
            alt="Banco de Bogot√° Logo" 
          />
        </div>

        <div className="auth-header">
          <h1>Iniciar Sesi√≥n</h1>
          <p>Ingresa a tu cuenta para continuar</p>
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="form-group">
            <label htmlFor="email">Correo Electr√≥nico</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="tu@email.com"
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Contrase√±a</label>
            <div className="password-wrapper">
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Ingresa tu contrase√±a"
                required
                disabled={loading}
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
                disabled={loading}
              >
                {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
              </button>
            </div>
          </div>

          <div className="forgot-password">
            <a href="/forgot-password">¬øOlvidaste tu contrase√±a?</a>
          </div>

          <button 
            type="submit" 
            className="submit-button"
            disabled={loading}
          >
            {loading ? (
              <>
                <span className="loading-spinner"></span>
                Iniciando sesi√≥n...
              </>
            ) : (
              'Iniciar Sesi√≥n'
            )}
          </button>
        </form>

        <div className="auth-footer">
          <p>
            ¬øNo tienes una cuenta?{' '}
            <a href="/register">Reg√≠strate aqu√≠</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Login;
