                      notifications.map(notification => (
                        <div
                          key={notification.id}
                          className={`notification-item ${notification.leida ? 'read' : 'unread'}`}
                          onClick={() => handleNotificationClick(notification)}
                        >
                          <div className="notification-content">
                            <p className="notification-message">{notification.mensaje}</p>
                            <span className="notification-time">
                              {formatRelativeTime(notification.created_at)}
                            </span>
                          </div>
                          {!notification.leida && <div className="unread-dot"></div>}
                        </div>
                      ))
                    )}
                  </div>
                </div>
              )}
            </div>

            {/* Usuario */}
            <div className="user-menu">
              <div className="user-info">
                <span className="user-name">{user?.nombre}</span>
                <span className="user-role">{user?.rol}</span>
              </div>
              <button onClick={handleLogout} className="btn btn-secondary">
                Cerrar Sesi√≥n
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="dashboard-main">
        {/* Bienvenida */}
        <div className="welcome-section">
          <h2>¬°Bienvenido, {user?.nombre}!</h2>
          <p>Aqu√≠ puedes gestionar tus solicitudes de aprobaci√≥n</p>
        </div>

        {/* Estad√≠sticas */}
        <div className="stats-grid">
          <StatsCard
            title="Total Solicitudes"
            value={stats?.total || 0}
            icon="üìä"
            color="primary"
          />
          <StatsCard
            title="Pendientes"
            value={stats?.pendientes || 0}
            icon="‚è≥"
            color="warning"
          />
          <StatsCard
            title="Aprobadas"
            value={stats?.aprobadas || 0}
            icon="‚úÖ"
            color="success"
          />
          <StatsCard
            title="Rechazadas"
            value={stats?.rechazadas || 0}
            icon="‚ùå"
            color="danger"
          />
        </div>

        {/* Acciones R√°pidas */}
        <Card className="quick-actions">
          <h3>Acciones R√°pidas</h3>
          <div className="actions-grid">
            <button
              className="action-btn action-btn-primary"
              onClick={() => navigate('/requests/new')}
            >
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <line x1="12" y1="5" x2="12" y2="19"></line>
                <line x1="5" y1="12" x2="19" y2="12"></line>
              </svg>
              <span>Nueva Solicitud</span>
            </button>

            <button
              className="action-btn action-btn-secondary"
              onClick={() => navigate('/requests')}
            >
              <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
                <polyline points="14 2 14 8 20 8"></polyline>
                <line x1="16" y1="13" x2="8" y2="13"></line>
