import api from './api';

const requestService = {
  // Obtener tipos de solicitud
  getTypes: async () => {
    try {
      const response = await api.get('/requests/types');
      return response.data;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error de conexi贸n' };
    }
  },

  // Obtener aprobadores
  getApprovers: async () => {
    try {
      const response = await api.get('/requests/approvers');
      return response.data;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error de conexi贸n' };
    }
  },

  // Obtener estad铆sticas
  getStats: async () => {
    try {
      const response = await api.get('/requests/stats');
      return response.data;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error de conexi贸n' };
    }
  },

  // Obtener solicitudes
  getAll: async (filters = {}) => {
    try {
      const params = new URLSearchParams();
      if (filters.estado) params.append('estado', filters.estado);
      if (filters.limit) params.append('limit', filters.limit);
      if (filters.offset) params.append('offset', filters.offset);

      const response = await api.get(`/requests?${params.toString()}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error de conexi贸n' };
    }
  },

  // Obtener solicitud por ID
  getById: async (id) => {
    try {
      const response = await api.get(`/requests/${id}`);
      return response.data;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error de conexi贸n' };
    }
  },

  //  CREAR SOLICITUD CON ARCHIVO
  create: async (data, file) => {
    try {
      // Crear FormData para enviar archivo
      const formData = new FormData();
      formData.append('titulo', data.titulo);
      formData.append('descripcion', data.descripcion);
      formData.append('tipo_solicitud_id', data.tipo_solicitud_id);
      formData.append('responsable_id', data.responsable_id);

      // Agregar archivo si existe
      if (file) {
        formData.append('file', file);
      }

      const response = await api.post('/requests', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error de conexi贸n' };
    }
  },

  //  DESCARGAR ARCHIVO
  downloadFile: async (id) => {
    try {
      const response = await api.get(`/requests/${id}/download`, {
        responseType: 'blob', // Importante para archivos
      });
      return response;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error al descargar archivo' };
    }
  },

  // Actualizar solicitud (editar)
  update: async (id, data) => {
    try {
      const response = await api.put(`/requests/${id}`, data);
      return response.data;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error de conexi贸n' };
    }
  },

  // Actualizar estado (aprobar/rechazar)
  updateStatus: async (id, data) => {
    try {
      const response = await api.patch(`/requests/${id}/status`, data);
      return response.data;
    } catch (error) {
      throw error.response?.data || { success: false, message: 'Error de conexi贸n' };
    }
  },
};

export default requestService;
