import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import requestService from '../services/requestService';
import Card from '../components/Card';
import Badge from '../components/Badge';
import Modal from '../components/Modal';
import LoadingSpinner from '../components/LoadingSpinner';
import { formatDateTime, formatRelativeTime, getStatusColor, getStatusIcon } from '../utils/formatters';
import '../styles/RequestDetail.css';

const RequestDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useAuth();

  const [request, setRequest] = useState(null);
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [modalAction, setModalAction] = useState(null);
  const [comment, setComment] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  // Estado para archivos adjuntos (simulado - adaptar seg√∫n tu backend)
  const [attachedFile, setAttachedFile] = useState(null);

  useEffect(() => {
    loadRequestData();
    if (location.state?.message) {
      setSuccessMessage(location.state.message);
      setTimeout(() => setSuccessMessage(''), 5000);
      window.history.replaceState({}, document.title);
    }
  }, [id]);

  const loadRequestData = async () => {
    try {
      setLoading(true);
      const response = await requestService.getById(id);
      setRequest(response.data.solicitud);
      setHistory(response.data.historial);

      // Simular archivo adjunto (adaptar seg√∫n tu backend)
      if (response.data.solicitud.archivo_adjunto) {
        setAttachedFile({
          nombre: response.data.solicitud.archivo_adjunto,
          tipo: response.data.solicitud.archivo_tipo || 'application/pdf',
          tamano: response.data.solicitud.archivo_tamano || 0
        });
      }
    } catch (error) {
      console.error('Error al cargar solicitud:', error);
      setError(error.message || 'Error al cargar la solicitud');
    } finally {
      setLoading(false);
    }
  };

  const canApprove = () => {
    if (!request || !user) return false;
    return (
      request.estado === 'pendiente' &&
      (user.rol === 'admin' || request.responsable_id === user.id)
    );
  };

  const handleOpenModal = (action) => {
    setModalAction(action);
    setComment('');
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setModalAction(null);
    setComment('');
  };

  const handleSubmitAction = async () => {
    if (!comment.trim() && modalAction === 'rechazado') {
      setError('Debes proporcionar un motivo para rechazar');
      return;
    }

    setSubmitting(true);
    setError('');

    try {
      await requestService.updateStatus(id, {
        estado: modalAction,
        comentario: comment.trim() || (modalAction === 'aprobado' ? 'Aprobado sin comentarios' : '')
      });

      await loadRequestData();
      handleCloseModal();
      setSuccessMessage(
        modalAction === 'aprobado'
          ? '‚úÖ Solicitud aprobada exitosamente'
          : '‚ùå Solicitud rechazada'
      );
      setTimeout(() => setSuccessMessage(''), 5000);
    } catch (error) {
      console.error('Error al actualizar solicitud:', error);
      setError(error.message || 'Error al actualizar la solicitud');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="request-detail-page">
        <LoadingSpinner fullScreen />
      </div>
    );
  }

  if (!request) {
    return (
      <div className="request-detail-page">
        <div className="page-header">
          <div className="header-content">
            <h1>Solicitud no encontrada</h1>
          </div>
        </div>
        <div className="page-content">
          <Card>
            <div className="error-state">
              <p>La solicitud que buscas no existe o no tienes permiso para verla.</p>
              <button className="btn btn-primary" onClick={() => navigate('/requests')}>
                Volver a solicitudes
              </button>
            </div>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="request-detail-page">
      <div className="page-header">
        <div className="header-content">
          <button className="back-btn" onClick={() => navigate('/requests')}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <path d="M19 12H5M12 19l-7-7 7-7"/>
            </svg>
            Volver
          </button>
          <h1>Detalle de Solicitud</h1>
        </div>
      </div>

      <div className="page-content">
        {successMessage && (
          <div className="alert alert-success">
            <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
            </svg>
            {successMessage}
          </div>
        )}

        <div className="detail-container">
          {/* COLUMNA PRINCIPAL */}
          <div className="detail-main">
            {/* HEADER DE LA SOLICITUD */}
            <Card>
              <div className="request-header-detail">
                <div className="request-title-section">
                  <h2>{request.titulo}</h2>
                  <Badge variant={getStatusColor(request.estado)} size="large">
                    {getStatusIcon(request.estado)} {request.estado.toUpperCase()}
                  </Badge>
                </div>

                <div className="request-code-section">
                  <span className="code-label">C√≥digo:</span>
                  <span className="code-value">{request.codigo_unico}</span>
                </div>
              </div>

              {/* INFORMACI√ìN GENERAL */}
              <div className="request-meta-grid">
                <div className="meta-item">
                  <span className="meta-label">üìã Tipo de Solicitud</span>
                  <span className="meta-value">{request.tipo_nombre}</span>
                </div>
                <div className="meta-item">
                  <span className="meta-label">üìÖ Fecha de Creaci√≥n</span>
                  <span className="meta-value">{formatDateTime(request.created_at)}</span>
                </div>
                <div className="meta-item">
                  <span className="meta-label">üë§ Solicitante</span>
                  <span className="meta-value">{request.solicitante_nombre}</span>
                </div>
                <div className="meta-item">
                  <span className="meta-label">‚úÖ Responsable</span>
                  <span className="meta-value">{request.responsable_nombre}</span>
                </div>
              </div>

              {/* DESCRIPCI√ìN */}
              <div className="request-description-section">
                <h3>üìù Descripci√≥n Detallada</h3>
                <div className="description-content">
                  {request.descripcion}
                </div>
              </div>

              {/* ARCHIVO ADJUNTO */}
              {attachedFile && (
                <div className="request-attachment-section">
                  <h3>üìé Archivo Adjunto</h3>
                  <div className="attachment-card">
                    <div className="attachment-icon">
                      {attachedFile.tipo.includes('pdf') ? 'üìÑ' : 'üìù'}
                    </div>
                    <div className="attachment-info">
                      <span className="attachment-name">{attachedFile.nombre}</span>
                      <span className="attachment-size">
                        {(attachedFile.tamano / 1024).toFixed(2)} KB
                      </span>
                    </div>
                    <button className="btn-download">
                      <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                        <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"></path>
                        <polyline points="7 10 12 15 17 10"></polyline>
                        <line x1="12" y1="15" x2="12" y2="3"></line>
                      </svg>
                      Descargar
                    </button>
                  </div>
                </div>
              )}

              {/* BOTONES DE ACCI√ìN */}
              {canApprove() && (
                <div className="action-buttons">
                  <button className="btn btn-success" onClick={() => handleOpenModal('aprobado')}>
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                      <polyline points="22 4 12 14.01 9 11.01"></polyline>
                    </svg>
                    Aprobar Solicitud
                  </button>
                  <button className="btn btn-danger" onClick={() => handleOpenModal('rechazado')}>
                    <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                      <circle cx="12" cy="12" r="10"></circle>
                      <line x1="15" y1="9" x2="9" y2="15"></line>
                      <line x1="9" y1="9" x2="15" y2="15"></line>
                    </svg>
                    Rechazar Solicitud
                  </button>
                </div>
              )}
            </Card>
          </div>

          {/* COLUMNA LATERAL - INFORMACI√ìN Y HISTORIAL */}
          <div className="detail-sidebar">
            {/* INFORMACI√ìN ADICIONAL */}
            <Card>
              <h3 className="sidebar-title">‚ÑπÔ∏è Informaci√≥n Adicional</h3>
              <div className="info-list">
                <div className="info-item">
                  <span className="info-label">Email del Solicitante</span>
                  <span className="info-value">{request.solicitante_email || 'No disponible'}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">Email del Responsable</span>
                  <span className="info-value">{request.responsable_email || 'No disponible'}</span>
                </div>
                <div className="info-item">
                  <span className="info-label">√öltima Actualizaci√≥n</span>
                  <span className="info-value">{formatRelativeTime(request.updated_at)}</span>
                </div>
              </div>
            </Card>

            {/* HISTORIAL */}
            <Card>
              <h3 className="sidebar-title">üïê Historial de Cambios</h3>
              <div className="history-timeline">
                {history.length === 0 ? (
                  <div className="no-history">
                    <p>Sin historial de cambios</p>
                  </div>
                ) : (
                  history.map((item, index) => (
                    <div key={item.id || index} className="timeline-item">
                      <div className="timeline-dot"></div>
                      <div className="timeline-content">
                        <div className="timeline-header">
                          <span className="timeline-action">{item.accion}</span>
                          <span className="timeline-date">
                            {formatRelativeTime(item.created_at)}
                          </span>
                        </div>
                        <p className="timeline-user">Por: {item.usuario_nombre}</p>
                        {item.comentario && (
                          <p className="timeline-comment">"{item.comentario}"</p>
                        )}
                        {item.estado_anterior && item.estado_nuevo && (
                          <div className="timeline-status-change">
                            <Badge variant={getStatusColor(item.estado_anterior)} size="small">
                              {item.estado_anterior}
                            </Badge>
                            <span className="arrow">‚Üí</span>
                            <Badge variant={getStatusColor(item.estado_nuevo)} size="small">
                              {item.estado_nuevo}
                            </Badge>
                          </div>
                        )}
                      </div>
                    </div>
                  ))
                )}
              </div>
            </Card>
          </div>
        </div>
      </div>

      {/* MODAL DE APROBACI√ìN/RECHAZO */}
      <Modal
        isOpen={showModal}
        onClose={handleCloseModal}
        title={modalAction === 'aprobado' ? '‚úÖ Aprobar Solicitud' : '‚ùå Rechazar Solicitud'}
        size="medium"
      >
        <div className="modal-form">
          {error && (
            <div className="alert alert-error">
              <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
              {error}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="comment" className="form-label">
              {modalAction === 'aprobado' ? 'Comentario (opcional)' : 'Motivo del rechazo *'}
            </label>
            <textarea
              id="comment"
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              className="form-textarea"
              placeholder={
                modalAction === 'aprobado'
                  ? 'A√±ade un comentario sobre tu aprobaci√≥n...'
                  : 'Explica por qu√© rechazas esta solicitud...'
              }
              rows="4"
            />
          </div>

          <div className="modal-actions">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={handleCloseModal}
              disabled={submitting}
            >
              Cancelar
            </button>
            <button
              type="button"
              className={`btn ${modalAction === 'aprobado' ? 'btn-success' : 'btn-danger'}`}
              onClick={handleSubmitAction}
              disabled={submitting}
            >
              {submitting ? (
                <>
                  <LoadingSpinner size="small" />
                  <span>Procesando...</span>
                </>
              ) : (
                <span>{modalAction === 'aprobado' ? 'Aprobar' : 'Rechazar'}</span>
              )}
            </button>
          </div>
        </div>
      </Modal>
    </div>
  );
};

export default RequestDetail;
