import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import authService from '../services/authService';
import LoadingSpinner from '../components/LoadingSpinner';
import { validateEmail } from '../utils/validation';
import '../styles/Auth.css';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [devToken, setDevToken] = useState('');
  const [copied, setCopied] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setDevToken('');
    setCopied(false);

    if (!email) {
      setError('El email es requerido');
      return;
    }

    if (!validateEmail(email)) {
      setError('Email inv√°lido');
      return;
    }

    setLoading(true);

    try {
      const response = await authService.forgotPassword(email);
      setSuccess(true);

      if (response.devToken) {
        setDevToken(response.devToken);
      }
    } catch (err) {
      setError(err.message || 'Error al procesar la solicitud');
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(devToken);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1 className="auth-title">¬øOlvidaste tu contrase√±a?</h1>
          <p className="auth-subtitle">
            Ingresa tu email y te enviaremos instrucciones para recuperar tu contrase√±a
          </p>
        </div>

        {error && (
          <div className="alert alert-error">
            <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
            {error}
          </div>
        )}

        {success && (
          <div className="alert alert-success">
            <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
            </svg>
            <div>
              <strong>‚úÖ Solicitud procesada</strong>
              <p style={{ marginTop: '8px', fontSize: '14px' }}>
                Si el email existe en nuestro sistema, recibir√°s las instrucciones.
              </p>
            </div>
          </div>
        )}

        {/* TOKEN MEJORADO CON DISE√ëO PROFESIONAL */}
        {devToken && (
          <div style={{
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            borderRadius: '16px',
            padding: '24px',
            marginTop: '20px',
            marginBottom: '20px',
            boxShadow: '0 8px 32px rgba(102, 126, 234, 0.3)',
          }}>
            {/* Header */}
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              marginBottom: '16px',
            }}>
              <span style={{ fontSize: '24px' }}>üîß</span>
              <span style={{
                color: 'white',
                fontSize: '18px',
                fontWeight: '600',
              }}>
                Modo Desarrollo
              </span>
            </div>

            {/* Descripci√≥n */}
            <p style={{
              color: 'rgba(255, 255, 255, 0.95)',
              fontSize: '14px',
              marginBottom: '12px',
            }}>
              Tu token de recuperaci√≥n:
            </p>

            {/* CAJA DEL TOKEN */}
            <div style={{
              background: 'rgba(255, 255, 255, 0.15)',
              backdropFilter: 'blur(10px)',
              border: '2px solid rgba(255, 255, 255, 0.25)',
              borderRadius: '12px',
              padding: '16px',
              marginBottom: '16px',
              maxHeight: '120px',
              overflowY: 'auto',
            }}>
              <code style={{
                color: 'white',
                fontFamily: '"Courier New", Courier, monospace',
                fontSize: '14px',
                fontWeight: '500',
                wordBreak: 'break-all',
                lineHeight: '1.8',
                display: 'block',
                userSelect: 'all',
              }}>
                {devToken}
              </code>
            </div>

            {/* BOT√ìN COPIAR */}
            <button
              onClick={copyToClipboard}
              style={{
                width: '100%',
                padding: '14px',
                background: 'white',
                color: '#667eea',
                border: 'none',
                borderRadius: '10px',
                fontSize: '15px',
                fontWeight: '600',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                gap: '8px',
                transition: 'all 0.2s ease',
                boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'translateY(-2px)';
                e.currentTarget.style.boxShadow = '0 6px 16px rgba(0, 0, 0, 0.15)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.style.boxShadow = '0 4px 12px rgba(0, 0, 0, 0.1)';
              }}
            >
              {copied ? (
                <>
                  <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                  </svg>
                  ¬°Copiado!
                </>
              ) : (
                <>
                  <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
                    <path d="M8 3a1 1 0 011-1h2a1 1 0 110 2H9a1 1 0 01-1-1z" />
                    <path d="M6 3a2 2 0 00-2 2v11a2 2 0 002 2h8a2 2 0 002-2V5a2 2 0 00-2-2 3 3 0 01-3 3H9a3 3 0 01-3-3z" />
                  </svg>
                  Copiar Token
                </>
              )}
            </button>

            {/* Texto de ayuda */}
            <p style={{
              color: 'rgba(255, 255, 255, 0.85)',
              fontSize: '13px',
              textAlign: 'center',
              marginTop: '12px',
              marginBottom: '0',
            }}>
              Usa este token en la p√°gina de restablecimiento de contrase√±a
            </p>
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form" noValidate>
          <div className="form-group">
            <label htmlFor="email" className="form-label">
              Correo Electr√≥nico
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-input"
              placeholder="tu@email.com"
              autoComplete="email"
              disabled={loading || success}
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary btn-full"
            disabled={loading || success}
          >
            {loading ? (
              <>
                <LoadingSpinner size="small" />
                <span>Procesando...</span>
              </>
            ) : success ? (
              'Solicitud Enviada'
            ) : (
              'Recuperar Contrase√±a'
            )}
          </button>
        </form>

        {success && (
          <div style={{ marginTop: '20px', textAlign: 'center' }}>
            <Link 
              to="/reset-password" 
              style={{ 
                color: '#1a73e8', 
                textDecoration: 'none',
                fontWeight: '500',
                fontSize: '15px',
              }}
            >
              Ya tengo mi token ‚Üí
            </Link>
          </div>
        )}

        <div className="auth-footer">
          <Link 
            to="/login" 
            style={{ 
              color: '#1a73e8',
              textDecoration: 'none',
              fontWeight: '500',
            }}
          >
            ‚Üê Volver al inicio de sesi√≥n
          </Link>
        </div>
      </div>
    </div>
  );
};

