import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import authService from '../services/authService';
import LoadingSpinner from '../components/LoadingSpinner';
import { validateEmail } from '../utils/validation';
import '../styles/Auth.css';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);
  const [devToken, setDevToken] = useState(''); // Para localhost

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess(false);
    setDevToken('');

    // Validaci√≥n
    if (!email) {
      setError('El email es requerido');
      return;
    }

    if (!validateEmail(email)) {
      setError('Email inv√°lido');
      return;
    }

    setLoading(true);

    try {
      const response = await authService.forgotPassword(email);

      setSuccess(true);

      // Si estamos en desarrollo y el servidor devuelve el token
      if (response.devToken) {
        setDevToken(response.devToken);
      }

    } catch (err) {
      setError(err.message || 'Error al procesar la solicitud');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1 className="auth-title">¬øOlvidaste tu contrase√±a?</h1>
          <p className="auth-subtitle">
            Ingresa tu email y te enviaremos instrucciones para recuperar tu contrase√±a
          </p>
        </div>

        {error && (
          <div className="alert alert-error">
            <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
            </svg>
            {error}
          </div>
        )}

        {success && (
          <div className="alert alert-success">
            <svg width="20" height="20" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
            </svg>
            <div>
              <strong>‚úÖ Solicitud procesada</strong>
              <p style={{ marginTop: '8px', fontSize: '14px' }}>
                Si el email existe en nuestro sistema, recibir√°s las instrucciones.
              </p>
            </div>
          </div>
        )}

        {/* Token para desarrollo (localhost) */}
        {devToken && (
          <div className="alert" style={{ 
            backgroundColor: '#fff3cd', 
            borderColor: '#ffc107',
            color: '#856404'
          }}>
            <strong>üîß Modo Desarrollo</strong>
            <p style={{ marginTop: '8px', fontSize: '14px' }}>
              Tu token de recuperaci√≥n:
            </p>
            <div style={{ 
              marginTop: '8px',
              padding: '12px',
              backgroundColor: '#fff',
              border: '1px solid #ffc107',
              borderRadius: '4px',
              fontFamily: 'monospace',
              fontSize: '12px',
              wordBreak: 'break-all'
            }}>
              {devToken}
            </div>
            <p style={{ marginTop: '8px', fontSize: '12px' }}>
              Copia este token y √∫salo en la p√°gina de restablecimiento.
            </p>
          </div>
        )}

        <form onSubmit={handleSubmit} className="auth-form" noValidate>
          <div className="form-group">
            <label htmlFor="email" className="form-label">
              Correo Electr√≥nico
            </label>
            <input
              type="email"
              id="email"
              name="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="form-input"
              placeholder="tu@email.com"
              autoComplete="email"
              disabled={loading || success}
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary btn-full"
            disabled={loading || success}
          >
            {loading ? (
              <>
                <LoadingSpinner size="small" />
                <span>Procesando...</span>
              </>
            ) : success ? (
              'Solicitud Enviada'
            ) : (
              'Recuperar Contrase√±a'
            )}
          </button>
        </form>

        {success && (
          <div style={{ marginTop: '20px', textAlign: 'center' }}>
            <Link to="/reset-password" className="auth-link">
              Ya tengo mi token ‚Üí
            </Link>
          </div>
        )}

        <div className="auth-footer">
          <Link to="/login" className="auth-link">
            ‚Üê Volver al inicio de sesi√≥n
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ForgotPassword;
